import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import avatar from '../../img/avatar.png';
import { signout } from '../../utils/Icons';
import { menuItems } from '../../utils/menuItems';
const speech = require('@google-cloud/speech');

function Navigation({ active, setActive }) {
    const [isListening, setIsListening] = useState(false);
    const [recognizedMessage, setRecognizedMessage] = useState('');

    useEffect(() => {
        if (isListening) {
            startListening();
        }
        return () => {
            setIsListening(false);
        };
    }, [isListening]);

    const handleMenuItemClick = (item) => {
        if (item.link.startsWith('/')) {
            setActive(item.id);
        } else {
            window.open(item.link, '_blank');
        }
    };

    const handleMicClick = () => {
        setIsListening((prev) => !prev);
    };

    const startListening = async () => {
        const client = new speech.SpeechClient();
        const config = {
            encoding: 'LINEAR16',
            sampleRateHertz: 16000,
            languageCode: 'en-US',
        };
        const request = {
            config: config,
            interimResults: false,
        };
        const streamingRecognize = client.streamingRecognize(request);
        streamingRecognize.on('data', (response) => {
            const message = response.results[0].alternatives[0].transcript.trim().toLowerCase();
            console.log('Recognized:', message);
            setRecognizedMessage(message); // Store the recognized message
            const menuItem = menuItems.find((item) => item.title.toLowerCase() === message);
            if (menuItem) {
                handleMenuItemClick(menuItem);
            }
        });
        streamingRecognize.on('error', (error) => {
            console.error(error);
        });
    };

    return (
        <NavStyled>
            <div className="user-con">
                <img src={avatar} alt="" />
                <div className="text">
                    <h2>Mike</h2>
                    <p>Your Money</p>
                </div>
            </div>
            <ul className="menu-items">
                {menuItems.map((item) => (
                    <li
                        key={item.id}
                        onClick={() => handleMenuItemClick(item)}
                        className={active === item.id ? 'active' : ''}
                    >
                        {item.icon}
                        <span>{item.title}</span>
                    </li>
                ))}
                <li onClick={handleMicClick}>
                    <span>🎙️<b>MIC</b></span>
                </li>
            </ul>
            <div className="bottom-nav">
                <li>
                    {signout} Sign Out
                </li>
            </div>
            {recognizedMessage && (
                <div className="recognized-message">{recognizedMessage}</div>
            )}
        </NavStyled>
    );
}
const NavStyled = styled.nav`
    padding: 2rem 1.5rem;
    width: 374px;
    height: 100%;
    background: rgba(252, 246, 249, 0.78);
    border: 3px solid #FFFFFF;
    backdrop-filter: blur(4.5px);
    border-radius: 32px;
    display: flex;
    flex-direction: column;
    justify-content: space-between;
    gap: 2rem;
    .user-con{
        height: 100px;
        display: flex;
        align-items: center;
        gap: 1rem;
        img{
            width: 80px;
            height: 80px;
            border-radius: 50%;
            object-fit: cover;
            background: #fcf6f9;
            border: 2px solid #FFFFFF;
            padding: .2rem;
            box-shadow: 0px 1px 17px rgba(0, 0, 0, 0.06);
        }
        h2{
            color: rgba(34, 34, 96, 1);
        }
        p{
            color: rgba(34, 34, 96, .6);
        }
    }

    .menu-items{
        flex: 1;
        display: flex;
        flex-direction: column;
        li{
            display: grid;
            grid-template-columns: 40px auto;
            align-items: center;
            margin: .6rem 0;
            font-weight: 500;
            cursor: pointer;
            transition: all .4s ease-in-out;
            color: rgba(34, 34, 96, .6);
            padding-left: 1rem;
            position: relative;
            i{
                color: rgba(34, 34, 96, 0.6);
                font-size: 1.4rem;
                transition: all .4s ease-in-out;
            }
        }
    }

    .active{
        color: rgba(34, 34, 96, 1) !important;
        i{
            color: rgba(34, 34, 96, 1) !important;
        }
        &::before{
            content: "";
            position: absolute;
            left: 0;
            top: 0;
            width: 4px;
            height: 100%;
            background: #222260;
            border-radius: 0 10px 10px 0;
        }
    }
`;

export default Navigation;
