import React, { useState, useMemo, useEffect } from 'react';
import styled from "styled-components";
import bg from './img/bg.png';
import { MainLayout } from './styles/Layouts';
import Orb from './Components/Orb/Orb';
import Navigation from './Components/Navigation/Navigation';
import Dashboard from './Components/Dashboard/Dashboard';
import Income from './Components/Income/Income';
import Expenses from './Components/Expenses/Expenses';
import alanBtn from "@alan-ai/alan-sdk-web";
import { useGlobalContext } from './context/globalContext';

function App() {
  const [active, setActive] = useState(1);
  const [micOn, setMicOn] = useState(false);

  const global = useGlobalContext();
  console.log(global);

  const displayData = () => {
    switch(active){
      case 1:
        return <Dashboard />;
      case 2:
        return <Dashboard />;
      case 3:
        return <Income />;
      case 4: 
        return <Expenses />;
      default: 
        return <Dashboard />;
    }
  };

  const orbMemo = useMemo(() => {
    return <Orb />;
  },[]);

  useEffect(() => {
    const alanKey = '92e15a0c0d7aaffbff45826a41532f5d2e956eca572e1d8b807a3e2338fdd0dc/stage';
    alanBtn({
      key: alanKey,
      onCommand: (commandData) => {
        const { command } = commandData;
        switch (command) {
          case 'turnOnMic':
            setMicOn(true);
            alanBtn().playText('Microphone turned on');
            break;
          case 'addTransaction':
            const { transactionName, amount } = commandData;
            alanBtn().playText(`Adding transaction: ${transactionName} with amount: ${amount}`);
            // Add logic to handle adding the transaction
            break;
          case 'openContacts':
            alanBtn().playText('Opening contacts');
            break;
          case 'openGovernmentScheme':
            alanBtn().playText('Opening government schemes');
            break;
          default:
            alanBtn().playText('Command not recognized');
            break;
        }
      },
      onMicUnlocked: () => {
        setMicOn(true);
      },
      onMicLocked: () => {
        setMicOn(false);
      },
    });
  }, []);

  return (
    <AppStyled bg={bg} className="App">
      {micOn ? <MicIndicator>Mic is on</MicIndicator> : <MicIndicator>Mic is off</MicIndicator>}
      {orbMemo}
      <MainLayout>
        <Navigation active={active} setActive={setActive} />
        <main>
          {displayData()}
        </main>
      </MainLayout>
    </AppStyled>
  );
}

const AppStyled = styled.div`
  height: 100vh;
  background-image: url(${props => props.bg});
  position: relative;
  main{
    flex: 1;
    background: rgba(252, 246, 249, 0.78);
    border: 3px solid #FFFFFF;
    backdrop-filter: blur(4.5px);
    border-radius: 32px;
    overflow-x: hidden;
    &::-webkit-scrollbar{
      width: 0;
    }
  }
`;

const MicIndicator = styled.div`
  position: absolute;
  top: 20px;
  right: 20px;
  background-color: #ffffff;
  padding: 10px;
  border-radius: 5px;
`;

export default App;
