import React, { useEffect } from 'react';
import styled from 'styled-components';
import { useGlobalContext } from '../../context/globalContext';
import { InnerLayout } from '../../styles/Layouts';
import IncomeItem from '../IncomeItem/IncomeItem';
import ExpenseForm from './ExpenseForm';

function Expenses({ currentForm, setCurrentForm }) {
    const {expenses, getExpenses, deleteExpense, totalExpenses} = useGlobalContext();

    useEffect(() =>{
        getExpenses();
    }, []);

    const handleDeleteWithVoice = (id, title) => {
        deleteExpense(id);
        // Voice feedback will be handled by the main App component
    };

    return (
        <ExpenseStyled>
            <InnerLayout>
                <h1>Expenses</h1>
                
                {/* Voice Form Indicator */}
                {currentForm === 'expense' && (
                    <VoiceFormIndicator>
                        <p>üéôÔ∏è Voice input active for new expense entry</p>
                        <button 
                            onClick={() => setCurrentForm(null)}
                            className="cancel-voice"
                        >
                            Cancel Voice Input
                        </button>
                    </VoiceFormIndicator>
                )}
                
                <h2 className="total-income">
                    Total Expense: <span>${totalExpenses()}</span>
                </h2>
                
                <div className="income-content">
                    <div className="form-container">
                        <ExpenseForm currentForm={currentForm} />
                    </div>
                    <div className="incomes">
                        {expenses.map((expense) => {
                            const {_id, title, amount, date, category, description, type} = expense;
                            return (
                                <IncomeItem
                                    key={_id}
                                    id={_id} 
                                    title={title} 
                                    description={description} 
                                    amount={amount} 
                                    date={date} 
                                    type={type}
                                    category={category} 
                                    indicatorColor="var(--color-delete)"
                                    deleteItem={() => handleDeleteWithVoice(_id, title)}
                                />
                            );
                        })}
                        {expenses.length === 0 && (
                            <EmptyState>
                                <p>No expense entries yet.</p>
                                <p>Say "Add expense" to create your first entry using voice commands!</p>
                            </EmptyState>
                        )}
                    </div>
                </div>
            </InnerLayout>
        </ExpenseStyled>
    );
}

const ExpenseStyled = styled.div`
    display: flex;
    overflow: auto;
    .total-income{
        display: flex;
        justify-content: center;
        align-items: center;
        background: beige;
        border: 5px solid darkgreen;
        box-shadow: 0px 1px 15px rgba(0, 0, 0, 0.06);
        border-radius: 20px;
        padding: 1rem;
        margin: 1rem 0;
        font-size: 2rem;
        gap: .5rem;
        span{
            font-size: 2.5rem;
            font-weight: bold;
            color: darkgreen;
        }
    }
    .income-content {
        display: flex;
        gap: 2rem;
        .form-container {
            flex: 1;
        }
        .incomes {
            flex: 2;
            display: flex;
            flex-direction: column;
            gap: 1rem;
        }
    }
`;

const VoiceFormIndicator = styled.div`
    display: flex;
    align-items: center;
    gap: 1rem;
    background: #fffbe6;
    border: 2px solid #ffb300;
    border-radius: 10px;
    padding: 0.75rem 1.5rem;
    margin-bottom: 1rem;
    p {
        margin: 0;
        font-weight: 500;
        color: #b26a00;
    }
    .cancel-voice {
        background: #ffb300;
        color: #fff;
        border: none;
        border-radius: 5px;
        padding: 0.5rem 1rem;
        cursor: pointer;
        font-weight: bold;
        transition: background 0.2s;
        &:hover {
            background: #ff9800;
        }
    }
`;

const EmptyState = styled.div`
    text-align: center;
    color: #888;
    margin-top: 2rem;
    p {
        margin: 0.5rem 0;
    }
`;
