import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import avatar from '../../img/avatar.png';
import { menuItems } from '../../utils/menuItems';

function Navigation({ setActive }) {
    const [isListening, setIsListening] = useState(false);
    const [recognizedMessage, setRecognizedMessage] = useState('');

    useEffect(() => {
        const handleSpeechRecognition = async () => {
            if (isListening) {
                try {
                    const response = await fetch('http://localhost:5000/recognize-speech');
                    const data = await response.text();
                    setRecognizedMessage(data);
                    handleMenuItemClick(data);
                } catch (error) {
                    console.error('Error recognizing speech:', error);
                }
            }
        };

        handleSpeechRecognition();
    }, [isListening]);

    const handleMenuItemClick = (menuItem) => {
        const selectedMenuItem = menuItems.find((item) => item.title.toLowerCase() === menuItem);
        if (selectedMenuItem) {
            setActive(selectedMenuItem.id);
            // Redirect to the corresponding page based on the menu item
            window.location.href = selectedMenuItem.link;
        }
    };

    const handleMicClick = () => {
        setIsListening(!isListening);
    };

    return (
        <NavStyled>
            <div className="user-con">
                <img src={avatar} alt="" />
                <div className="text">
                    <h2>Mike</h2>
                    <p>Your Money</p>
                </div>
            </div>
            <ul className="menu-items">
                {menuItems.map((item) => (
                    <li
                        key={item.id}
                        onClick={() => handleMenuItemClick(item.title.toLowerCase())}
                    >
                        {item.icon}
                        <span>{item.title}</span>
                    </li>
                ))}
                <li onClick={handleMicClick}>
                    <span>üéôÔ∏è<b>MIC</b></span>
                </li>
            </ul>
            {recognizedMessage && (
                <div className="recognized-message">{recognizedMessage}</div>
            )}
        </NavStyled>
    );
}

const NavStyled = styled.nav`
    padding: 2rem 1.5rem;
    width: 374px;
    height: 100%;
    background: rgba(252, 246, 249, 0.78);
    border: 3px solid #FFFFFF;
    backdrop-filter: blur(4.5px);
    border-radius: 32px;
    display: flex;
    flex-direction: column;
    justify-content: space-between;
    gap: 2rem;
    .user-con{
        height: 100px;
        display: flex;
        align-items: center;
        gap: 1rem;
        img{
            width: 80px;
            height: 80px;
            border-radius: 50%;
            object-fit: cover;
            background: #fcf6f9;
            border: 2px solid #FFFFFF;
            padding: .2rem;
            box-shadow: 0px 1px 17px rgba(0, 0, 0, 0.06);
        }
        h2{
            color: rgba(34, 34, 96, 1);
        }
        p{
            color: rgba(34, 34, 96, .6);
        }
    }

    .menu-items{
        flex: 1;
        display: flex;
        flex-direction: column;
        li{
            display: grid;
            grid-template-columns: 40px auto;
            align-items: center;
            margin: .6rem 0;
            font-weight: 500;
            cursor: pointer;
            transition: all .4s ease-in-out;
            color: rgba(34, 34, 96, .6);
            padding-left: 1rem;
            position: relative;
            i{
                color: rgba(34, 34, 96, 0.6);
                font-size: 1.4rem;
                transition: all .4s ease-in-out;
            }
        }
    }

    .active{
        color: rgba(34, 34, 96, 1) !important;
        i{
            color: rgba(34, 34, 96, 1) !important;
        }
        &::before{
            content: "";
            position: absolute;
            left: 0;
            top: 0;
            width: 4px;
            height: 100%;
            background: #222260;
            border-radius: 0 10px 10px 0;
        }
    }
`;

export default Navigation;
