import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import { useGlobalContext } from '../../context/globalContext';
import { InnerLayout } from '../../styles/Layouts';
import ExpenseForm from './ExpenseForm'; // Import ExpenseForm component
import IncomeItem from '../IncomeItem/IncomeItem';

function Expenses() {
    const { expenses, getExpenses, addExpense, deleteExpense, totalExpenses, error } = useGlobalContext();
    const [newExpense, setNewExpense] = useState(null);

    useEffect(() => {
        getExpenses();
    }, [getExpenses]);

    // Callback function to handle form submission
    const handleFormSubmit = (expense) => {
        addExpense(expense);
        setNewExpense(expense); // Set the newly added expense
    };

    return (
        <ExpenseStyled>
            <InnerLayout>
                <h1>Expenses</h1>
                {error && <p className="error">{error}</p>}
                <h2 className="total-income">Total Expense: <span>${totalExpenses()}</span></h2>
                <div className="income-content">
                    <div className="form-container">
                        {/* Render ExpenseForm component */}
                        <ExpenseForm onSubmit={handleFormSubmit} />
                    </div>
                    <div className="incomes">
                        {/* Render newly added expense */}
                        {newExpense && (
                            <IncomeItem
                                key={newExpense._id}
                                id={newExpense._id}
                                title={newExpense.title}
                                description={newExpense.description}
                                amount={newExpense.amount}
                                date={newExpense.date}
                                type={newExpense.type}
                                category={newExpense.category}
                                indicatorColor="var(--color-green)"
                                deleteItem={deleteExpense}
                            />
                        )}
                        {/* Render existing expenses */}
                        {expenses.map((expense) => (
                            <IncomeItem
                                key={expense._id}
                                id={expense._id}
                                title={expense.title}
                                description={expense.description}
                                amount={expense.amount}
                                date={expense.date}
                                type={expense.type}
                                category={expense.category}
                                indicatorColor="var(--color-green)"
                                deleteItem={deleteExpense}
                            />
                        ))}
                    </div>
                </div>
            </InnerLayout>
        </ExpenseStyled>
    );
}

const ExpenseStyled = styled.div`
    display: flex;
    overflow: auto;
    .total-income{
        display: flex;
        justify-content: center;
        align-items: center;
        background: beige;
        border: 5px solid darkgreen;
        box-shadow: 0px 1px 15px rgba(0, 0, 0, 0.06);
        border-radius: 20px;
        padding: 1rem;
        margin: 1rem 0;
        font-size: 2rem;
        gap: .5rem;
        span{
            font-size: 2.5rem;
            font-weight: 800;
            color: var(--color-green);
        }
    }
    .income-content{
        display: flex;
        gap: 2rem;
        .incomes{
            flex: 1;
        }
    }
`;

export default Expenses;
