import React, { useContext, useState, useEffect } from "react";
import axios from 'axios';

const BASE_URL = "http://localhost:5000/api/v1/";

const GlobalContext = React.createContext();

export const GlobalProvider = ({ children }) => {
    const [data, setData] = useState({ incomes: [], expenses: [] });
    const [error, setError] = useState(null);

    const fetchData = async () => {
        try {
            const [incomesResponse, expensesResponse] = await Promise.all([
                axios.get(`${BASE_URL}get-incomes`),
                axios.get(`${BASE_URL}get-expenses`)
            ]);
            setData({ incomes: incomesResponse.data, expenses: expensesResponse.data });
        } catch (error) {
            setError('Failed to fetch data. Please try again.');
            console.error('Error fetching data:', error);
        }
    };

    useEffect(() => {
        fetchData(); // Fetch initial data
    }, []);

    const addIncome = async (income) => {
        try {
            await axios.post(`${BASE_URL}add-income`, income);
            fetchData(); // Fetch updated data after adding income
        } catch (error) {
            setError('Failed to add income. Please try again.');
            console.error('Error adding income:', error);
        }
    };

    const addExpense = async (expense) => {
        try {
            await axios.post(`${BASE_URL}add-expense`, expense);
            fetchData(); // Fetch updated data after adding expense
        } catch (error) {
            setError('Failed to add expense. Please try again.');
            console.error('Error adding expense:', error);
        }
    };

    const clearError = () => {
        setError(null);
    };

    return (
        <GlobalContext.Provider value={{
            data,
            error,
            addIncome,
            addExpense,
            clearError
        }}>
            {children}
        </GlobalContext.Provider>
    );
};

export const useGlobalContext = () => {
    return useContext(GlobalContext);
};

export default GlobalContext;
